export const PartnersLogoSection: React.FC<Props> = ({
  title = "Our Partners",
  tiers = [],
}) => {
  return (
    <div className="bg-white">
      {/* Title Section */}
      {title && (
        <section className="py-8 md:py-12">
          <div className="container mx-auto px-4">
            <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold text-primary text-center">
              {title}
            </h2>
          </div>
        </section>
      )}

      {/* Tiers Section */}
      <section className="pb-12 md:pb-16 lg:pb-20">
        <div className="container mx-auto px-4">
          <div className="space-y-6 md:space-y-8">
            {tiers.map((tier, tierIndex) => {
              return (
                <div
                  key={tierIndex}
                  className="bg-white rounded-lg overflow-hidden"
                  style={{
                    border: `4px solid ${tier.outlineColor || "#E5E7EB"}`,
                  }}
                >
                  <div className="flex flex-col md:flex-row items-stretch min-h-[200px]">
                    {/* Star section */}
                    <div
                      className="flex-shrink-0 w-full md:w-64 lg:w-72 flex items-center justify-center p-6 md:p-8"
                      style={{
                        backgroundColor: tier.starColor || "#3B82F6",
                      }}
                    >
                      {/* Star shape with text - made bigger */}
                      <div className="relative w-40 h-40 md:w-48 md:h-48 lg:w-52 lg:h-52">
                        {/* Star SVG */}
                        <MakeAWishStar className="w-full h-full text-white drop-shadow-lg" />
                        {/* Text overlay with rotation */}
                        <div className="absolute inset-0 flex items-center justify-center">
                          <div
                            className="text-center px-4 transform -rotate-12"
                            style={{ transformOrigin: "center center" }}
                          >
                            <p
                              className="font-bold leading-tight"
                              style={{
                                color: tier.starColor || "#3B82F6",
                                fontSize: "clamp(0.85rem, 2.5vw, 1.1rem)",
                              }}
                            >
                              {tier.name?.split(" ").map((word, i, arr) => (
                                <React.Fragment key={i}>
                                  {word}
                                  {i < arr.length - 1 && <br />}
                                </React.Fragment>
                              ))}
                            </p>
                          </div>
                        </div>
                      </div>
                    </div>

                    {/* Logos section - centered on mobile, one per line */}
                    <div className="flex-1 p-6 md:p-8 flex items-center justify-center">
                      {tier.logos && tier.logos.length > 0 ? (
                        <div className="flex flex-col md:flex-row md:flex-wrap items-center justify-center gap-8 md:gap-10 lg:gap-12 w-full">
                          {tier.logos.map((logo, logoIndex) => {
                            const logoImage = (
                              <img
                                src={logo.imageUrl}
                                alt={logo.altText || "Partner logo"}
                                className="max-h-14 md:max-h-16 lg:max-h-20 w-auto object-contain transition-transform hover:scale-110 duration-300"
                                style={{
                                  maxWidth: "200px",
                                }}
                              />
                            );

                            return (
                              <div
                                key={`${tier.name}-${logoIndex}`}
                                className="flex justify-center items-center w-full md:w-auto"
                              >
                                {logo.link ? (
                                  <a
                                    href={logo.link}
                                    target="_blank"
                                    rel="noopener noreferrer" 
                                    className="inline-block"
                                  >
                                    {logoImage}
                                  </a>
                                ) : (
                                  logoImage
                                )}
                              </div>
                            );
                          })}
                        </div>
                      ) : (
                        <div className="text-gray-400 text-xl w-full text-center">
                          Logos
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </section>
    </div>
  );
};

export default PartnersLogoSection;